mapper.select:
select d.*,c.name category_name from dish d left outer join category c on d.category_id = c.id


        and d.name like "%"#{name}"%"


        and d.category_id = #{categoryId}


        and d.status = #{status}


order by d.create_time desc

mapper.update:
update dish
name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description}, status = #{status},
where id=#{id}

mapper.select:
select * from dish


        and name like "%"#{name}"%"


        and category_id = #{categoryId}


        and status = #{status}


order by create_time desc
